        <style>
			.left-item {width:100px;display: inline-block;margin-right: 6px; font-weight:500;}
			.right-item{ display:inline-block; margin-left: 10px;overflow-wrap: break-word;}
			#qrcode img {margin: 8px auto;}
			.timeline {
			  position: relative;
			  display: grid;
			  grid-template-columns: 8px 1fr;
			  column-gap: 16px;
			  padding: 0px 10px 10px 10px;
			  margin-top: 20px;
			}

			.timeline h2, p {
			  margin: 0;
			}

			.timeline-step-knob {
			  grid-column: 1 / 2;
			  width: 100%;
			  aspect-ratio: 1 / 1; 
			  z-index: 1;
			  
			  background: #ec1b2e;
			  border-radius: 50%;
			}

			.timeline-step-title {
			  grid-column: 2 / 3;
			}
			
			.timeline-step-title h5{
				margin: -6px 0px;
				color: rgba(0, 0, 0, .5);
				font-weight: 500;
			}

			.timeline-step-description {
			  grid-column: 2 / 3;
			  margin-bottom: 1.5em;
			}
			
			.timeline-step-description div{
				font-size:14px;
			}
			
			.timeline-step-video-btn{
				background: transparent;
				border-radius:6px;
				vertical-align: middle;	
			}
			

			.timeline-step-video-btn svg{
				width: 20px;
				height: 20px;
			}
			
			.timeline-step-video-btn:hover{
				background-color: rgba(0, 0, 0, .5);
				
			}
			
			.timeline-step-description h5{
				margin: 7px 0px;
			}

			.timeline-line {
			  position: absolute;
			  
			  grid-column: 1 / 2;
			  justify-self: center;
			  
			  height: 100%;
			  width: 2px;
			  z-index: 0;
			  
			  background-color: rgba(0, 0, 0, .1);
			}
			
			/* The Modal (background) */
			.modal {
			  display: none; /* Hidden by default */
			  position: fixed; /* Stay in place */
			  z-index: 1; /* Sit on top */
			  padding-top: 100px; /* Location of the box */
			  left: 0;
			  top: 0;
			  width: 100%; /* Full width */
			  height: 100%; /* Full height */
			  overflow: auto; /* Enable scroll if needed */
			  background-color: rgb(0,0,0); /* Fallback color */
			  background-color: rgba(0,0,0,0.4); /* Black w/ opacity */
			}

			/* Modal Content */
			.modal-content {
			  position: relative;
			  background-color: #fefefe;
			  margin: auto;
			  padding: 0;
			  width: 96%;
			  box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2),0 6px 20px 0 rgba(0,0,0,0.19);
			  border-radius: 6px;
			}

			/* Add Animation */
			@-webkit-keyframes animatetop {
			  from {top:-300px; opacity:0} 
			  to {top:0; opacity:1}
			}

			@keyframes animatetop {
			  from {top:-300px; opacity:0}
			  to {top:0; opacity:1}
			}

			/* The Close Button */
			.close {
			  float: right;
			  font-size: 28px;
			  font-weight: bold;
			  width: 32px;
			}

			.close:hover,
			.close:focus {
			  color: #000;
			  text-decoration: none;
			  cursor: pointer;
			}

			.modal-header {
			  padding: 2px 10px;
			  border-bottom: solid 1px #DDE3F5;
			}
			
			.modal-header h4{
				margin: 12px 0px;
			}

			.modal-body {padding: 16px;}
			
			.video-list{
				margin-bottom: 15px;
				margin-top: 15px;
				text-align: left;
				border-top: solid 1px #DDE3F5;
				padding-top: 12px;
			}
			
			.video-list button {
				padding: 8px 16px;
				margin: 0px 10px;
				border-radius: 4px;
			}
			
			.video-list input.ux-btn-group-item {
			  opacity: 0;
			  margin-left: -14px;
			}
			.video-list input.ux-btn-group-item:checked + label {
			  background-image: linear-gradient(#febdd5, #fff6fc) !important;
			  outline: none;
			  border: 1px solid #ec1b2e;
			}
			.video-list label {
			  border-style: solid;
			  border-width: 1px 1px 1px 1px;
			  border-color: transparent;
			  background-color: #f1f1f1;
			  padding: 5px 10px;
			  border-radius:4px;
			  margin-right: 10px;
			}
			.video-list label:active {
			  outline: none;
			  border: 1px solid #ec1b2e;
			  background-image: linear-gradient(#e1e3e5, #f7f8f8) !important;
			  box-shadow: inset 2px 2px 16px rgba(143, 151, 158, 0.3), inset 2px 2px 14px rgba(112, 128, 128, 0.9), 0 0 0 #606265;
			  transform: scale(1, 0.97);
			}
			.video-list label:focus {
			  outline: none;
			}

			input[type=checkbox]:focus + label,
			input[type=radio]:focus + label {
			  border: 1px solid #ec1b2e;
			}
			
			.processing-tag{
				font-size: 11px;
				background: #ebebeb;
				padding: 2px 5px;
				border-radius: 4px;
				color: #0b7bdb;
			}
			
			.order-status {
				font-size: 11px;
				padding: 2px 5px;
				border-radius: 4px;
				background: #ebebeb;
				color: #d95a00;
				margin-left: 10px;
			}
			
			.warning-tag{
				background: #f9b61f;
				color : #fff;
			}
			
			.error-tag{
				background: #ec1b2e;
				color : #fff;
			}
			
			.success-tag{
				background: #2ac10f;
				color : #fff;
			}

        </style>
