		<script src="/js/qrcode.min.js"></script>
		<script>
			document.addEventListener("DOMContentLoaded", function(event) { 
			  //do work
			  new QRCode(document.getElementById("qrcode"), {
					text: "{{$qrCode}}",
					width: 240,
					height: 240,
					colorDark : "#000000",
					colorLight : "#ffffff",
					correctLevel : QRCode.CorrectLevel.H
				});
				
			
			 //Countdown timmer 
			 var countDownDate = new Date("{{$data->expires}}").getTime();

			// Update the count down every 1 second
			var x = setInterval(function() {

			  // Get today's date and time
			  var now = new Date().getTime();

			  // Find the distance between now and the count down date
			  var distance = countDownDate - now;

			  // Time calculations for days, hours, minutes and seconds
			  var days = Math.floor(distance / (1000 * 60 * 60 * 24));
			  var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
			  var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
			  var seconds = Math.floor((distance % (1000 * 60)) / 1000);

			  // Display the result in the element with id="demo"
			  document.getElementById("countdown").innerHTML = days + "ngày - " + hours + ":"
			  + minutes + ":" + seconds + "giây ";

			  // If the count down is finished, write some text
			  if (distance < 0) {
				clearInterval(x);
				document.getElementById("countdown").innerHTML = "Đã Quá Hạn";
			  }
			}, 1000);
			});
			
			var modal = document.getElementById("myModal");
			var span = document.getElementsByClassName("close")[0];
			span.onclick = function() {
			  modal.style.display = "none";
			}
			
			window.onclick = function(event) {
			  if (event.target == modal) {
				modal.style.display = "none";
			  }
			}
			
			function loadVideoUrl(url){
				console.log
				document.getElementById("videoDetail").innerHTML='<video width="100%" controls><source src="'+url+'" type="video/mp4">Your browser does not support the video tag.</video>';
			}
			
			function showVideoDialog(title_id,videoArr){
				modal.style.display = "block";
				switch(title_id){
					case 1:
						document.getElementById("dialogTitle").innerHTML="Video : Khách hàng gửi hàng";
						break;
					case 2:
					document.getElementById("dialogTitle").innerHTML="Video : Khách hàng nhận hàng";
					break;
					case 3:
					document.getElementById("dialogTitle").innerHTML="Video : Bưu tá lấy hàng";
					break;
					case 4:
					document.getElementById("dialogTitle").innerHTML="Video : Bưu tá gửi hàng";
					break;
					case 5:
					document.getElementById("dialogTitle").innerHTML="Video : Bưu tá cho phép mở tủ trên APP VTMan";
					break;
					case 6:
					document.getElementById("dialogTitle").innerHTML="Video : Thu hồi";
					break;
					case 7:
					document.getElementById("dialogTitle").innerHTML="Video : Quá thời hạn lần 1 và đã gửi tin nhắn cho bưu tá";
					break;
					case 8:
					document.getElementById("dialogTitle").innerHTML="Video : Quá thời hạn lần 2 và đã  gửi tin nhắn cho bưu tá";
					break;
					case 9:
					document.getElementById("dialogTitle").innerHTML="Video : Khách hàng yêu cầu khóa khẩn cấp";
					break;
					case 10:
					document.getElementById("dialogTitle").innerHTML="Video : Khách hàng mở khóa khẩn cấp";
					break;
					case 11:
					document.getElementById("dialogTitle").innerHTML="Video : Quản trị viên yêu cầu khóa khẩn cấp";
					break;
					case 12:
					document.getElementById("dialogTitle").innerHTML="Video : Quản trị viên mở khóa khẩn cấp";
					break;
					case 13:
					document.getElementById("dialogTitle").innerHTML="Video : Hủy đơn hàng";
					break;
					case 14:
					document.getElementById("dialogTitle").innerHTML="Video : Khởi tạo đơn";
					break;
					default: 
						document.getElementById("dialogTitle").innerHTML="Video : -";
					break;
				}
				
				let listVideo = "";
				for(let i = 0; i<videoArr.length;i++){
					listVideo +='<input type="radio" id="option '+(i+1)+'" class="ux-btn-group-item" name="option" onclick="loadVideoUrl(\''+videoArr[i]+'\')"';
					if(i==0)
						listVideo +='checked ';
					
					listVideo += '/><label for="option '+(i+1)+'">Video '+(i+1)+'</label>';
					
				}
				document.getElementById("videoItemList").innerHTML=listVideo;
				loadVideoUrl(videoArr[0]);
				
			}
			
			
